package com.example.indexablelistview;
public class MyDataClass {

	public MyDataClass() {
		// TODO Auto-generated constructor stub
	}
	
	int word_id;
	String english_word;
	String native_word;
	String translitaration;
	String mp3_id;
	public int getWord_id() {
		return word_id;
	}
	public void setWord_id(int word_id) {
		this.word_id = word_id;
	}
	public String getEnglish_word() {
		return english_word;
	}
	public void setEnglish_word(String english_word) {
		this.english_word = english_word;
	}
	public String getNative_word() {
		return native_word;
	}
	public void setNative_word(String native_word) {
		this.native_word = native_word;
	}
	public String getTranslitaration() {
		return translitaration;
	}
	public void setTranslitaration(String translitaration) {
		this.translitaration = translitaration;
	}
	public String getMp3_id() {
		return mp3_id;
	}
	public void setMp3_id(String mp3_id) {
		this.mp3_id = mp3_id;
	}
		
	
}
